INSERT INTO `wp_posts` VALUES (219, 1, '2025-04-14 05:21:41', '2025-04-13 21:21:41', '{\n    \"cim::cim_about_image\": {\n        \"value\": \"http://localhost:70/wp-content/uploads/2025/04/2ca1e9_b486aadd404841799620aabb67a2a49bmv2_\\u770b\\u56fe\\u738b.jpg\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2025-04-13 21:21:41\"\n    },\n    \"cim::cim_about_bg_image\": {\n        \"value\": \"http://localhost:70/wp-content/uploads/2025/04/2ca1e9_8749d883aeb148a28497684573fdd8bfmv2.webp\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2025-04-13 21:21:41\"\n    },\n    \"cim::cim_about_text\": {\n        \"value\": \"Canadian Innovative Materials (CIM) is a global leader  in wear solutions, specializing in advanced materials engineered to withstand extreme abrasion in the mining, energy, and construction industries. Our proprietary technologies - including Tungsten Carbide Overlay (WCO), High Chrome White Iron (HCWI), Silicon Carbide Ceramic (SiC), and Chrome Carbide Overlay (CCO) - provide exceptional wear resistance, significantly enhancing equipment performance and longevity.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2025-04-13 21:21:41\"\n    },\n    \"cim::cim_about_button_url\": {\n        \"value\": \"/technologies\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2025-04-13 21:21:41\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '4a610446-752c-4702-a432-dfeb15109362', '', '', '2025-04-14 05:21:41', '2025-04-13 21:21:41', '', 0, 'http://localhost:70/2025/04/14/4a610446-752c-4702-a432-dfeb15109362/', 0, 'customize_changeset', '', 0);
