INSERT INTO `wp_posts` VALUES (317, 1, '2025-04-14 14:13:15', '2025-04-14 06:13:15', '<!-- wp:paragraph -->\n<p id=\"viewer-foo\">We had a fantastic time at MINExpo 2024 in Las Vegas! It was a great opportunity to meet so many industry professionals, showcase our advanced materials, and discuss innovative solutions for the mining industry. The turnout was incredible, and we are grateful to everyone who stopped by our booth!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p id=\"viewer-0zfba95\">Special thanks to all the attendees who engaged with us and took interest in our Titanium Carbide (TiC) + Chrome White Iron (CWI), Sintered TiC, Crack-Free CCO, and PTAWCO Technology. Your insights and enthusiasm inspire us to keep pushing the boundaries of wear protection.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p id=\"viewer-nszar97\">Stay tuned - we\'ll be sharing more updates and exciting projects soon.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":316} -->\n<figure class=\"wp-block-image\"><img src=\"http://localhost:70/wp-content/uploads/2025/04/2ca1e9_1dc89f8c33f94358a9fe1c8485fa47d8mv2.jpg\" alt=\"\" class=\"wp-image-316\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":315} -->\n<figure class=\"wp-block-image\"><img src=\"http://localhost:70/wp-content/uploads/2025/04/2ca1e9_b3d5135b3b434ea4bd26f06eae069077mv2.jpg\" alt=\"\" class=\"wp-image-315\"/></figure>\n<!-- /wp:image -->', 'Amazing Experience at MINExpo 2024', '', 'inherit', 'closed', 'closed', '', '314-revision-v1', '', '', '2025-04-14 14:13:15', '2025-04-14 06:13:15', '', 314, 'http://localhost:70/?p=317', 0, 'revision', '', 0);
